import {useState} from 'react';

function Square(props) {
  const [mark, setMark] = useState(null);
  function handleClick() {
    setMark(props.currentMark);
  }
  return (<button className="square" onClick={handleClick}> {mark}</button>);
}

function Board(props) {
  const [marks, setMarks] = useState(Array(9).fill(null))
  return (
  <>
  <div className="board-row">
    <Square mark={marks[0]}></Square>
    <Square mark={marks[1]}></Square>
    <Square mark={marks[2]}></Square>
  </div>
  <div className="board-row">
    <Square mark={marks[3]}></Square>
    <Square mark={marks[4]}></Square>
    <Square mark={marks[5]}></Square>
  </div>
  <div className="board-row">
    <Square mark={marks[6]}></Square>
    <Square mark={marks[7]}></Square>
    <Square mark={marks[8]}></Square>
  </div>
  </>);
}

export default function Game() {
  var listBoards = [{id : 1, title : "Go to move start", }];

}
