import { useState } from 'react';

function Square(props) {
  return <button className="square" onClick={props.handleFunction}>{props.value}</button>;
}

export default function Board() {
  const [squares, setSquares] = useState(Array(9).fill(null));
  function handleMarkClick(){
    squaresCopy = squares.slice();
    squaresCopy[0] = "X";
    setSquares(squaresCopy);
  }
  return (
    <>
      <div className="board-row">
        <Square handleFunction={handleMarkClick} value={squares[0]} />
        <Square handleFunction={handleMarkClick} value={squares[1]} />
        <Square handleFunction={handleMarkClick} value={squares[2]} />
      </div>
      <div className="board-row">
        <Square handleFunction={handleMarkClick} value={squares[3]} />
        <Square handleFunction={handleMarkClick} value={squares[4]} />
        <Square handleFunction={handleMarkClick} value={squares[5]} />
      </div>
      <div className="board-row">
        <Square handleFunction={handleMarkClick} value={squares[6]} />
        <Square handleFunction={handleMarkClick} value={squares[7]} />
        <Square handleFunction={handleMarkClick} value={squares[8]} />
      </div>
    </>
  );
}
