import {useState} from 'react';

function Square(props) {
  function handleClick() {
    handleMarks()
  }
  return (<button className="square" onClick={handleClick}> {props.mark}</button>);
}

export default function Board(props) {
  const [isXTurn, setIsXTurn] = useState(False)
  const [currentMark, setCurrentMark] = useState("O")
  const [marks, setMarks] = useState(Array(9).fill(null))
  if (isXTurn){
    setCurrentMark("X")
  }
  else{
    setCurrentMark("O")
  }
  function handleMarks(index){
    newMarks = marks.slice();
    newMarks[index] = currentMark;
  }
  return (
  <>
  <div className="board-row">
    <Square index={0} mark={marks[0]}></Square>
    <Square index={1} mark={marks[1]}></Square>
    <Square index={2} mark={marks[2]}></Square>
  </div>
  <div className="board-row">
    <Square index={3} mark={marks[3]}></Square>
    <Square index={4} mark={marks[4]}></Square>
    <Square index={5} mark={marks[5]}></Square>
  </div>
  <div className="board-row">
    <Square index={6} mark={marks[6]}></Square>
    <Square index={7} mark={marks[7]}></Square>
    <Square index={8} mark={marks[8]}></Square>
  </div>
  </>);
}
